import React from 'react'
import Layout from '../components/MyLayout.js'
import Gallery from "react-grid-gallery";
import Lightbox from 'react-images';
import IMAGES from '../images/products.json'

export default class About extends React.Component {
  state = { currentImage: 0, lightboxIsOpen: false, width: window.innerWidth }
  componentWillMount() {
    window.addEventListener('resize', this.handleWindowSizeChange);
  }
  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
  }
  render() {
    const isMobile = this.state.width <= 500;
    if (isMobile) {
      return (
        <Layout>
          {IMAGES.map(image => {
            return <img src={image.thumbnail.thumbnail}
            style={{
              width: "100%",
              border: 0,
              verticalAlign: "middle",
              marginBottom: 5
            }}
            />
          })}
        </Layout>
      )
    }
    return (
      <Layout>
        <div style={{
          display: "block",
          minHeight: 1,
          width: "100%",
          overflow: "auto"
        }}>
          <Gallery onClick={this.openLightbox}
            enableImageSelection={false}
            enableLightbox={false}
            rowHeight={450}
            onClickThumbnail={this.openLightbox}
            images={IMAGES.map(({thumbnail}) => ({...thumbnail, src: ""}) )} />
        </div>
        <Lightbox images={IMAGES.map(({lightbox}) => lightbox )}
          backdropClosesModal
          preventScroll={false}
          onClose={this.closeLightbox}
          onClickPrev={this.gotoPrevious}
          onClickNext={this.gotoNext}
          currentImage={this.state.currentImage}
          isOpen={this.state.lightboxIsOpen}
        />
      </Layout>
    )
  }
  handleWindowSizeChange = () => {
    this.setState({ width: window.innerWidth });
  }
  openLightbox = (currentImage, event) => {
    this.setState({ currentImage, lightboxIsOpen: true });
  }
  closeLightbox = () => {
    this.setState({
      currentImage: 0,
      lightboxIsOpen: false,
    });
  }
  gotoPrevious = () => {
    this.setState({
      currentImage: this.state.currentImage - 1,
    });
  }
  gotoNext = () => {
    this.setState({
      currentImage: this.state.currentImage + 1,
    });
  }
}
