import React from 'react'
import Link from 'next/link'

export default class Header extends React.Component {
  styles = {
    linkStyle: {
      marginRight: 15
    }
  }
  render() {
    let {styles} = this;
    return(
      <div>
        <HeaderMobile />
        <div className='header-desktop'>
          <Link href="/">
            <a style={styles.linkStyle}>Home</a>
          </Link>
          <Link href="/products">
            <a style={styles.linkStyle}>Products</a>
          </Link>
          <Link href="/architecture">
            <a style={styles.linkStyle}>Architecture</a>
          </Link>
          <Link href="/about">
            <a style={styles.linkStyle}>About</a>
          </Link>
        </div>
      </div>
    )
  }
}

class HeaderMobile extends React.Component {
  state = {open: false}
  styles = {
    container:{
      position: 'absolute',
      top: 0,
      left: 0,
      zIndex: '99',
      opacity: 0.9,
      display:'flex',
      alignItems:'center',
      background: 'black',
      width: '100%',
      color: 'white',
      fontFamily:'Lobster',
    },
    logo: {
      margin: '0 auto',
    },
    linkStyle: {
      marginRight: 15
    }
  }
  render() {
    let {open} = this.state, {styles} = this;

    const menu = ['About Us','Our Products','Services','FAQ','Contact Us']
    const menuItems = menu.map((val,index)=>{
      return (
        <MenuItem
          key={index}
          delay={`${index * 0.1}s`}
          onClick={()=>{this.handleLinkClick();}}>{val}</MenuItem>)
    });


    return(
      <div>
        <div className='header-mobile' style={styles.container}>
          <MenuButton open={this.state.menuOpen} onClick={()=>this.handleMenuClick()} color='white'/>
          <div style={styles.logo}>Menu</div>
        </div>
        <Menu open={this.state.menuOpen}>
          {menuItems}
        </Menu>
      </div>
    )
  }
  handleLinkClick() {
    this.setState({menuOpen: false});
  }
  handleMenuClick() {
    this.setState({open:!this.state.open});
  }
}

/* MenuItem.jsx*/
class MenuItem extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      hover:false,
    }
  }

  handleHover(){
    this.setState({hover:!this.state.hover});
  }

  render(){
    const styles={
      container: {
        opacity: 0,
        animation: '1s appear forwards',
        animationDelay:this.props.delay,
      },
      menuItem:{
        fontFamily:`'Open Sans', sans-serif`,
        fontSize: '1.2rem',
        padding: '1rem 0',
        margin: '0 5%',
        cursor: 'pointer',
        color: this.state.hover? 'gray':'#fafafa',
        transition: 'color 0.2s ease-in-out',
        animation: '0.5s slideIn forwards',
        animationDelay:this.props.delay,

      },
      line: {
        width: '90%',
        height: '1px',
        background: 'gray',
        margin: '0 auto',
        animation: '0.5s shrink forwards',
        animationDelay:this.props.delay,

      }
    }
    return(
      <div style={styles.container}>
        <div
          style={styles.menuItem}
          onMouseEnter={()=>{this.handleHover();}}
          onMouseLeave={()=>{this.handleHover();}}
          onClick={this.props.onClick}
        >
          {this.props.children}
        </div>
      <div style={styles.line}/>
    </div>
    )
  }
}

class Menu extends React.Component {
  constructor(props){
    super(props);
    this.state={
      open: this.props.open? this.props.open:false,
    }
  }

  componentWillReceiveProps(nextProps){
    if(nextProps.open !== this.state.open){
      this.setState({open:nextProps.open});
    }
  }

  render(){
    const styles={
      container: {
        position: 'absolute',
        top: 0,
        left: 0,
        height: this.state.open? '100%': 0,
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        background: 'black',
        opacity: 0.95,
        color: '#fafafa',
        transition: 'height 0.3s ease',
        zIndex: 2,
      },
      menuList: {
        paddingTop: '3rem',
      }
    }
    return(
      <div style={styles.container}>
        {
          this.state.open?
            <div style={styles.menuList}>
              {this.props.children}
            </div>:null
        }
      </div>
    )
  }
}
