import React from 'react'
import Link from 'next/link'

export default class Header extends React.Component {
  render() {
    return(
      <div>
        <HeaderMobile />
        <div className='header-desktop'>
          <Link href="/">
            <a style={styles.linkStyle}>Home</a>
          </Link>
          <Link href="/products">
            <a style={styles.linkStyle}>Products</a>
          </Link>
          <Link href="/architecture">
            <a style={styles.linkStyle}>Architecture</a>
          </Link>
          <Link href="/about">
            <a style={styles.linkStyle}>About</a>
          </Link>
        </div>
      </div>
    )
  }
}

class HeaderMobile extends React.Component {
  state = {open: false}
  render() {
    let {open} = this.state
    return(
      <div>
        <div className='header-mobile' style={styles.container}>
          <div style={styles.logo} onClick={() => this.setState({open: !open})}>Menu</div>
        </div>
        <Menu open={this.state.menuOpen}>
          <div>
            <Link href="/products">
              <a style={styles.linkStyle}>Products</a>
            </Link>
            <Link href="/architecture">
              <a style={styles.linkStyle}>Architecture</a>
            </Link>
            <Link href="/about">
              <a style={styles.linkStyle}>About</a>
            </Link>
          </div>
        </Menu>
      </div>
    )
  }
}

class Menu extends React.Component {
  constructor(props){
    super(props);
    this.state={
      open: this.props.open? this.props.open:false,
    }
  }

  componentWillReceiveProps(nextProps){
    if(nextProps.open !== this.state.open){
      this.setState({open:nextProps.open});
    }
  }

  render(){
    const styles={
      container: {
        position: 'absolute',
        top: 0,
        left: 0,
        height: this.state.open? '100%': 0,
        width: '100vw',
        display: 'flex',
        flexDirection: 'column',
        background: 'black',
        opacity: 0.95,
        color: '#fafafa',
        transition: 'height 0.3s ease',
        zIndex: 2,
      },
      menuList: {
        paddingTop: '3rem',
      }
    }
    return(
      <div style={styles.container}>
        {
          this.state.open?
            <div style={styles.menuList}>
              {this.props.children}
            </div>:null
        }
      </div>
    )
  }
}

const styles = {
  container:{
    position: 'absolute',
    top: 0,
    left: 0,
    zIndex: '99',
    opacity: 0.9,
    display:'flex',
    alignItems:'center',
    background: 'black',
    width: '100%',
    color: 'white',
    fontFamily:'Lobster',
  },
  logo: {
    margin: '0 auto',
  },
  // body: {
  //   display: 'flex',
  //   flexDirection: 'column',
  //   alignItems: 'center',
  //   width: '100vw',
  //   height: '100vh',
  //   filter: this.state.menuOpen ? 'blur(2px)':null,
  //   transition: 'filter 0.5s ease',
  // },
  linkStyle: {
    marginRight: 15
  }
}
