import Header from './Header'
const Layout = props => (
  <div class='layout'>
    <Header />
    {props.children}
    <style jsx global>{`
      body {
        background: black;
        color: white;
      }
      @media only screen and (min-width: 501px) {
        body {
          padding: 0 16px;
        }
        .ReactGridGallery_tile-viewport:hover {
          background: black;
        }
        .ReactGridGallery_tile-viewport:hover img {
          opacity: 0.7;
        }
        .layout {
          margin: 20px;
        }
      }
    `}</style>
  </div>
)

export default Layout
