export default class About extends React.Component {
  state = { currentImage: 0, lightboxIsOpen: false }
  render() {
    return (
      <Layout>
        <div style={{
          display: "block",
          minHeight: 1,
          width: "100%",
          overflow: "auto"
        }}>
          <Gallery onClick={this.openLightbox}
            enableImageSelection={false}
            enableLightbox={false}
            rowHeight={450}
            onClickThumbnail={this.openLightbox}
            images={IMAGES.map(({thumbnail}) => ({...thumbnail, src: ""}) )} />
        </div>
        <Lightbox images={IMAGES.map(({lightbox}) => lightbox )}
          backdropClosesModal
          preventScroll={false}
          onClose={this.closeLightbox}
          onClickPrev={this.gotoPrevious}
          onClickNext={this.gotoNext}
          currentImage={this.state.currentImage}
          isOpen={this.state.lightboxIsOpen}
        />
      </Layout>
    )
  }
  openLightbox = (currentImage, event) => {
    this.setState({ currentImage, lightboxIsOpen: true });
  }
  closeLightbox = () => {
    this.setState({
      currentImage: 0,
      lightboxIsOpen: false,
    });
  }
  gotoPrevious = () => {
    this.setState({
      currentImage: this.state.currentImage - 1,
    });
  }
  gotoNext = () => {
    this.setState({
      currentImage: this.state.currentImage + 1,
    });
  }
}
