import Header from './Header'
const Layout = props => (
  <div className='layout'>
    <Header />
    {props.children}
    <style jsx global>{`
      body {
        background: black;
        color: white;
        margin: 0px;
        padding 0px;
      }
      .header-desktop {
        display: none;
      }
      @media only screen and (min-width: 501px) {
        .ReactGridGallery_tile-viewport:hover {
          background: black;
        }
        .ReactGridGallery_tile-viewport:hover img {
          opacity: 0.7;
        }
        .layout {
          padding: 0 16px;
        }
        .header-desktop {
          margin: 0 -16px;
        }
        .header-mobile {
          display: none;
        }
      }
    `}</style>
  </div>
)

export default Layout
