import Header from './Header'

const layoutStyle = {
  margin: 20
}

const Layout = props => (
  <div style={layoutStyle}>
    <Header />
    {props.children}
    <style jsx global>{`
      body {
        background: black;
        color: white;
      }
      @media only screen and (min-width: 500px) {
        body {
          padding: 0 16px;
        }
        .ReactGridGallery_tile-viewport:hover {
          background: black;
        }
        .ReactGridGallery_tile-viewport:hover img {
          opacity: 0.7;
        }
      }
    `}</style>
  </div>
)

export default Layout
